mapper.insert:
insert into approval values((select max(approval_no)+1 
from approval),#{emp_no},#{approval_choose_no},to_date(#{approval_submit_date},'yyyy-mm-dd hh24:mi:ss'),
#{approval_start_date},#{approval_end_date},#{approval_comment},null,null,#{approval_team_no},#{approval_mgr_no})

mapper.select:
select h.emp_name as emp_name,h.dept_no as dept_no,a.approval_no,t.emp_name as team_mgr_name,s.emp_name as mgr_name,a.approval_no,a.emp_no,a.approval_choose_no, to_char(a.approval_submit_date,'yyyy-mm-dd hh24:mi:ss') as approval_submit_date,a.approval_start_date, a.approval_end_date,a.approval_comment, to_char(a.approval_team_date,'yyyy-mm-dd hh24:mi:ss') as approval_team_date,a.approval_mgr_date from approval a
,(select emp_name,dept_no,emp_no,emp_mgr from emp) h, (select emp_name,emp_no from emp where emp_no=#{emp_no}) t,(select emp_name,emp_no from emp where job_no=3) s where h.emp_no=a.emp_no and h.emp_mgr=#{emp_no}
,(select emp_name,dept_no,emp_no from emp) h, (select emp_name,emp_no from emp where emp_mgr =0 and job_no != 3) t,(select emp_name,emp_no from emp where job_no=3) s where a.approval_team_date is not null and h.emp_no=a.emp_no;
