mapper.insert:

insert into approval values((select max(approval_no)+1 
from approval),#{emp_no},#{approval_choose_no},to_date(#{approval_submit_date},'yyyy-mm-dd hh24:mi:ss'),
#{approval_start_date},#{approval_end_date},#{approval_comment},null,null,#{approval_team_no},#{approval_mgr_no})

mapper.select:
select count(*) as listCount from approval a
,(select emp_name,dept_no,emp_no,emp_mgr from emp) h, (select emp_name,emp_no,emp_mgr from emp where emp_no=#{emp_no}) t,(select emp_name,emp_no from emp where job_no=3) s where h.emp_no=a.emp_no and h.emp_mgr=#{emp_no} and t.emp_mgr=s.emp_no and h.emp_name=#{emp_name}
,(select emp_name,dept_no,emp_no,emp_mgr from emp) h, (select emp_name,emp_no,emp_mgr from emp where emp_mgr=(select emp_no from emp where emp_no=#{emp_no})) t,(select emp_name,emp_no from emp where job_no=3) s where a.approval_team_date is not null and h.emp_no=a.emp_no and s.emp_no=#{emp_no} and t.emp_mgr=s.emp_no and a.emp_no=h.emp_no and h.emp_mgr=t.emp_no and h.emp_name=#{emp_name}
