mapper.insert:
insert into approval values((select max(approval_no)+1 
from approval),#{emp_no},#{approval_choose_no},to_date(#{approval_submit_date},'yyyy-mm-dd hh24:mi:ss'),
#{approval_start_date},#{approval_end_date},#{approval_comment},null,null)

mapper.select:
select approval_no,a.emp_no,approval_choose_no,to_char(approval_submit_date,'yyyy-mm-dd hh24:mi:ss') as approval_submit_date,approval_start_date,approval_end_date,approval_comment, to_char(approval_team_date,'yyyy-mm-dd hh24:mi:ss') as approval_team_date,approval_mgr_date
from approval
from approval a where a.emp_no=(select emp_no from emp where emp_mgr=#{emp_no})
