select.if:

	select * from (
	(select rownum as rn, A.* from (select * from client where client_contract = 'Y') A))
	where rn between #{startPage} and #{endPage}

	
		select * from (
		(select rownum as rn, A.* from (select * from client where emp_no = #{emp_no} and client_contract = 'Y') A))
		where rn between #{startPage} and #{endPage}

mapper.delete:

	update client
	set client_delete='Y'
	where client_no = #{client_no}

mapper.insert:

	insert into client_file values(
		(select max(client_file_no)+1 from client_file), #{client_no},
		#{client_original_file}, #{client_rename_file}
	)

mapper.update:

	update client
	set client_contract = 'Y'
	where client_no = #{client_no}

select.if:

select * from (
(select rownum as rn, A.* from (select * from client where client_contract = 'Y' and emp_no=#{emp_no} and client_company=#{client_company} order by client_enroll desc) A))
where rn between #{startPage} and #{endPage}


select * from (
		(select rownum as rn, A.* from (select * from client where client_contract = 'Y' and client_company=#{client_company} order by client_enroll desc) A))
		where rn between #{startPage} and #{endPage}
